import React, { useState, useEffect } from 'react';
import './ShowTransactions.css'

export let ShowTransactions = (props) => {

    let handleChange=(hawker,index)=>{

        let _hawker=hawker;
        console.log(_hawker,inputVal[index])
        props.offer(_hawker,parseInt(inputVal[index]))
    }

    let [inputVal, setInputVal] = useState(new Array(props.hawkers.length).map( e => ""))

    // let inputChangeHandler = (i) => {
    //     setInputVal(event.target.value)
    // }

    let inputChangeHandler = (i) => {
        return (event) => setInputVal(inputArray => inputArray.map( (e,j) => { return j === i ? event.target.value : e }))
    }




    let hawkerList=props.hawkers.map((hawker,i)=>
        <tr key={i}>
            <td >{hawker.name}</td>
            <td><input onChange={inputChangeHandler(i)} value={inputVal[i]}></input></td>
            <td>{hawker.amount}</td>
            <td> <button  onClick={handleChange.bind(this,hawker.name,i)}> Offer </button></td>
        </tr>)

        return(
            <div>
            <h3> Hawkers</h3>
            <hr />
            <table >
                <tbody>
                    <tr>
                        <th>Hawkers</th>
                        <th>Amount</th> 
                    </tr>
                    {hawkerList}
                </tbody>
            </table>
          </div>
        )
    } 


export default ShowTransactions;
